crude = 'Amna'

def cdu(crude, refinery_volume = None, lvn_gasolinepool = None, kero_gasolinepool = None):
    ### INPUT CRUDE ###
    #crude = 'Forties'
    #refinery_volume = 200
    
    ### Fractions for gaosline pool
    if refinery_volume is None:
        refinery_volume = 200
