                        next_gpw = gpw_calculation(i,k, *var, **refinery_config)
                        next_arb = pd.concat([next_arb,next_gpw], axis=1)
                        margin_headers = []
                        for j in [index for index in next_arb.columns if '_landed' in index]:
                           name = j[:4]+"_margin"
